	int calMode(List<String> devDetail, int n) {
		int maxValue = 0, maxCount = 0, i, j;

		for (i = 0; i < n; ++i) {
			int count = 0;
			for (j = 0; j < n; ++j) {
				if (devDetail.get(j) == devDetail.get(i))
					++count;
			}

			if (count > maxCount) {
				maxCount = count;
				maxValue = Integer.parseInt(devDetail.get(i));
			}
		}
		return maxValue;
	}


