			List<BabyDetail> babyDetailsLsit = inicuDao.getListFromMappedObjQuery(queryBabyDetails);
			Integer gestation = null;
			Calendar birthDateCal = null;
			Integer ageAtAssessment = null;
			Integer PMA = null;
